using System;using System.Collections;using System.Collections.Generic;using UnityEngine;namespace RedTeam {    public class ColliderEventRelayer : MonoBehaviour {        public Action<Collision> onCollisionEnter;        public Action<Collision> onCollisionExit;        public Action<Collision> onCollisionStay;        public Action<Collider> onTriggerEnter;        public Action<Collider> onTriggerExit;        public Action<Collider> onTriggerStay;        void OnCollisionEnter(Collision collision) {                        if (onCollisionEnter != null)                onCollisionEnter.Invoke(collision);        }        void OnCollisionExit(Collision collision) {            if (onCollisionExit != null)                onCollisionExit.Invoke(collision);        }        void OnCollisionStay(Collision collision) {            if (onCollisionStay != null)                onCollisionStay.Invoke(collision);        }        void OnTriggerEnter(Collider other) {            if (onTriggerEnter != null)                onTriggerEnter.Invoke(other);        }        void OnTriggerExit(Collider other) {            if (onTriggerExit != null)                onTriggerExit.Invoke(other);        }        void OnTriggerStay(Collider other) {            if (onTriggerStay != null)                onTriggerStay.Invoke(other);        }    }}